import React, { useContext, useEffect } from 'react';
import ParkingGrid from './components/ParkingGrid';
import CarForm from './components/CarForm';
import SlotForm from './components/SlotForm';
import LoginForm from './components/LoginForm';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { AuthContext } from './contexts/AuthContext';

function App() {
  const { isAuthenticated, login } = useContext(AuthContext);

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      login(token); // Verifica se há um token no localStorage e autentica automaticamente
    }
  }, [login]);

  return (
    <Container>
      {isAuthenticated ? (
        <>
          <Typography variant="h4" gutterBottom>
            Easy Parking - Vagas Disponíveis
          </Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} md={8}>
              <ParkingGrid />
            </Grid>
            <Grid item xs={12} md={4}>
              <Typography variant="h6">Cadastrar Carro</Typography>
              <CarForm />
              <Typography variant="h6" sx={{ mt: 5 }}>Cadastrar Vaga</Typography>
              <SlotForm />
            </Grid>
          </Grid>
        </>
      ) : (
        <>
          <Typography variant="h4" gutterBottom>
            Login
          </Typography>
          <LoginForm />
        </>
      )}
    </Container>
  );
}

export default App;
