import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import ParkingGrid from './components/ParkingGrid';
import CarForm from './components/CarForm';
import SlotForm from './components/SlotForm';
import Grid from '@mui/material/Grid';

function App() {
  const [currentPage, setCurrentPage] = useState('login'); // Alterna entre 'login', 'register', 'vagas'

  const handleLogin = () => {
    setCurrentPage('vagas');
  };

  const handleRegister = () => {
    setCurrentPage('vagas');
  };

  return (
    <Container>
      {currentPage === 'login' && (
        <>
          <Typography variant="h4" gutterBottom>
            Login
          </Typography>
          <LoginForm onSubmit={handleLogin} />
          <Button onClick={() => setCurrentPage('register')} fullWidth sx={{ mt: 2 }}>
            Não tem uma conta? Registre-se
          </Button>
        </>
      )}

      {currentPage === 'register' && (
        <>
          <Typography variant="h4" gutterBottom>
            Registrar
          </Typography>
          <RegisterForm onSubmit={handleRegister} />
          <Button onClick={() => setCurrentPage('login')} fullWidth sx={{ mt: 2 }}>
            Já tem uma conta? Faça Login
          </Button>
        </>
      )}

      {currentPage === 'vagas' && (
        <>
          <Typography variant="h4" gutterBottom>
            Easy Parking - Vagas Disponíveis
          </Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} md={8}>
              <ParkingGrid />
            </Grid>
            <Grid item xs={12} md={4}>
              <Typography variant="h6">Cadastrar Carro</Typography>
              <CarForm />
              <Typography variant="h6" sx={{ mt: 5 }}>Cadastrar Vaga</Typography>
              <SlotForm />
            </Grid>
          </Grid>
        </>
      )}
    </Container>
  );
}

export default App;
